--Create database

-- Clear table
drop table IF EXISTS "order_item";
drop table IF EXISTS "order";
drop table IF EXISTS "product_category";
drop table IF EXISTS "category";
drop table IF EXISTS "product";
drop table IF EXISTS "users";
drop table IF EXISTS "store";

-- Create user
CREATE TABLE "users" (
	id VARCHAR ( 50 ) NOT NULL,
	name VARCHAR ( 50 ) NOT NULL,
    email VARCHAR ( 50 ) unique NOT NULL,
	phone CHAR(10),
	address VARCHAR ( 255 ),
	picture text,
	created_at TIMESTAMP NOT NULL,
	updated_at TIMESTAMP NOt NULL,
	CONSTRAINT PK_Person PRIMARY KEY ("id")
);

CREATE TABLE "store"(
    sid VARCHAR ( 50 ) NOT NULL,
    address VARCHAR ( 255 ),
    phone CHAR(10) NOT NULL,
    store_name VARCHAR ( 50 ) NOT NULL,
    picture text,
    content TEXT NULL DEFAULT NULL,
    created_at TIMESTAMP NOT NULL,
	updated_at TIMESTAMP NOT NULL,
    CONSTRAINT PK_store PRIMARY KEY ("sid"),
    CONSTRAINT FK_users FOREIGN KEY ("sid") REFERENCES "users" ("id") ON DELETE CASCADE
);

CREATE TABLE "product"(
    pid serial NOT NULL,
    title VARCHAR ( 50 ) NOT NULL,
    price decimal(7,2) NOT NULL,
    quantity INT NOT NULL DEFAULT 0,
    "sid" VARCHAR ( 50 ) NOT NULL,
    created_at TIMESTAMP NOT NULL,
	updated_at TIMESTAMP NOT NULL,
    discount INT NOT NULL DEFAULT 0,
    img text not null,
    content text NULL DEFAULT NULL,
    unit VARCHAR ( 20 ) NOT NULL,
    CONSTRAINT PK_product PRIMARY KEY ("pid"),
    CONSTRAINT FK_store FOREIGN KEY ("sid") REFERENCES "store" ("sid") ON DELETE CASCADE
);

-- ID -> int
CREATE TABLE "category"(
    "id" serial NOT NULL,
    title VARCHAR ( 75 ) NOT NULL,
    CONSTRAINT PK_category PRIMARY KEY ("id")
);

CREATE TABLE "product_category"(
    "pid" INT NOT NULL,
    category_id INT NOT NULL,
    CONSTRAINT FK_categorys FOREIGN KEY ("category_id") REFERENCES "category" ("id") ON DELETE CASCADE,
    FOREIGN KEY ("pid") REFERENCES "product" ("pid") ON DELETE CASCADE,
    CONSTRAINT PK_product_category PRIMARY KEY ("pid", "category_id")
);

CREATE TABLE "order"(
    order_id VARCHAR ( 50 ) NOT NULL,
    cid VARCHAR ( 50 ) NOT NULL,
    "status" VARCHAR ( 255 ) NOT NULL,
    total decimal(7,2) NOT NULL DEFAULT 0,
    phone CHAR(10) ,
    address VARCHAR ( 255 ) ,
    delivery VARCHAR ( 255 ) NOT NULL,
    updated_at TIMESTAMP NOT NULL,
    created_at TIMESTAMP NOT NULL,
    FOREIGN KEY ("cid") REFERENCES "users" ("id") ON DELETE CASCADE,
    CONSTRAINT PK_order PRIMARY KEY ("order_id")
);

CREATE TABLE "order_item" (
    order_id VARCHAR ( 50 ) NOT NULL,
    pid INT NOT NULL,
    price decimal(7,2) NOT NULL DEFAULT 0,
    quantity INT NOT NULL DEFAULT 0,
    updated_at TIMESTAMP NOT NULL,
    created_at TIMESTAMP NOT NULL,
    FOREIGN KEY ("order_id") REFERENCES "order" ("order_id") ON DELETE CASCADE,
    FOREIGN KEY ("pid") REFERENCES "product" ("pid") ON DELETE CASCADE,
    CONSTRAINT PK_order_item PRIMARY KEY ("order_id", "pid")
);

